class DojoTest(unittest.TestCase):
    def test_true(self):
        self.assertTrue(main())
    
    def test_day_one_example(self):
        self.assertEqual(read_lines(input_example), 4361)
    def test_day_one(self):
        self.assertEqual(read_lines(input), 537732)

    def test_day_one_part2_example(self):
        self.assertEqual(read_lines_gear(input_example), 467835)

    def test_nuumber_in_line_should_return_true_left(self):
        self.assertEqual(return_number_in_line("467..114..", 2), 467)
    
    def test_nuumber_in_line_should_return_true_right(self):
        self.assertEqual(return_number_in_line("467..114..", 5), 114)

    def test_nuumber_in_line_should_return_true_middle(self):
        self.assertEqual(return_number_in_line("467..114..", 1), 467)

    def test_nuumber_in_line_should_return_none(self):
        self.assertEqual(return_number_in_line("46...114..", 3), 0)

    def test_nuumber_in_line_should_return_none_2(self):
        self.assertEqual(return_number_in_line("46...114..", 4), 0)


